    <!-- CONTACT FORM -->
    <form class="contact" id="form">
   <div class="container contact-form">
            <div class="contact-image">
                <img src="data:image/png;base64," alt="rocket_contact"/>
            </div>
            <form method="post">
                <h3 class="contact-title">Drop Us a Message</h3>
               <div class="row">
                    <div class="col-md-6">
                        <div class="form-group name-wrapper">
                            <input type="text" name="txtName" class="form-control" placeholder="Your Name *" value="" />
                        </div>
                        <div class="form-group mail-wrapper">
                            <input type="email" name="txtEmail" class="form-control" placeholder="Your Email *" value="" />
                        </div>
                        <div class="form-group phone-wrapper">
                            <input type="text" name="txtPhone" class="form-control" placeholder="Your Phone Number " value="" />
                        </div>
                        
                    </div>
                    <div class="col-md-6">
                        <div class="form-group message-wrapper">
                            <textarea name="txtMsg" class="form-control" placeholder="Your Message *" style="width: 100%; height: 150px;"></textarea>
                        </div>
                    </div>

                    <div class="col-md-6">
                      <div class="from-group">
                        <input type="checkbox">
                        <label class="contact-check-label" for="gridCheck">Subscribe to our newsletter</label>
                      </div>

                    <div class="form-group">
                            <input type="submit" name="btnSubmit" class="btnContact" value="Send Message" />
                        </div>
                     
                    </div>
                </div>
            </form>
         </div>
    </form>


    



