<html>
    <head>
        <meta charset="utf-8">
        <title>Register MotoWorld</title>

        <link rel="stylesheet" type="text/css" href="css/registerCss.css">

        <link href="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" rel="stylesheet" id="bootstrap-css">

<!------ Include the above in your HEAD tag ---------->
    </head>

<!------ Include the above in your HEAD tag ---------->


<body>
    <div style="background:linear-gradient(rgba(0, 0, 0, 0.2),rgba(0, 0, 0, 0.2)),url('https://images.unsplash.com/photo-1476067897447-d0c5df27b5df?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80'); background-repeat: no-repeat; background-size: cover; background-position: center center;">

        <div class="wrapper fadeInDown" >
            <div id="formContent" >
                <!-- Tabs Titles -->

                <!-- Icon -->
                <div class="fadeIn first contact-image">
                <img src="data:image/png;base64," id="icon" alt="User Icon" />
                </div>

                <!-- Register Form -->
                <form class="form_class" id="form">
                    <div class="row">
                        <div class="col-lg">
                            <input type="email" id="email" class="fadeIn second" name="email" placeholder="Email">
                            <input type="text" id="fname" class="fadeIn third" name="fname" placeholder="First name">
                            <input type="text" id="sname" class="fadeIn third" name="lname" placeholder="Last name">
                        </div>


                        <div class="col-lg">
                            <input type="text" id="nname" class="fadeIn second" name="nname" placeholder="Nickname">
                            <input type="password" id="password" class="fadeIn third" name="password" placeholder="Password">
                            <input type="password" id="confirmPassword" class="fadeIn third" name="cpassword" placeholder="Confirm password">
                        </div>
                    </div>

                    <div class="error-wrapper"><input type="submit" class="fadeIn fourth" value="Register"></div>

                </form>

                <!-- Remind Passowrd -->
                <div id="formFooter">
                    <a class="underlineHover" href="#" onclick="window.open('login.php', '_self')">Already have an account?</a>
                </div>

            </div>
        </div>
    </div>



    <script src="//maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js"></script>
    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
    <script type="text/javascript" src="scripts/register.js"></script>
</body>

</html>
